mapper.select:
SELECT

FROM user

    
        NAME LIKE CONCAT('%',#{name},'%')
    
    
        AND BIRTHDAY BETWEEN #{start}
    
    
        AND #{end}
    
    
        AND GENDER = #{gender}
    
    
        AND ADDRESS LIKE CONCAT('%',#{address},'%')
    
    
        AND PHONE LIKE CONCAT('%',#{phone},'%')
    

ORDER BY ${sort}

    DESC

LIMIT #{pageNumber}, #{size}

SELECT COUNT(*)
FROM user

    
        NAME LIKE CONCAT('%',#{name},'%')
    
    
        AND BIRTHDAY BETWEEN #{start}
    
    
        AND #{end}
    
    
        AND GENDER = #{gender}
    
    
        AND ADDRESS LIKE CONCAT('%',#{address},'%')
    
    
        AND PHONE LIKE CONCAT('%',#{phone},'%')
